document.addEventListener("DOMContentLoaded",(()=>{"yes"===localStorage.getItem("cookiesAccepted")?(cookies.style.display="none",cookiesOuter.style.display="none",console.log("cookies already accepted")):(cookies.style.display="block",cookiesOuter.style.display="block",console.log("user needs to accept cookies"),body.style.overflowY="hidden")})),cookiesButton.addEventListener("click",(()=>{myStorage.setItem("cookiesAccepted","yes"),console.log("cookies accepted"),cookies.style.display="none",cookiesOuter.style.display="none",body.style.overflowY="scroll"}));